import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useGigs } from '../context/GigContext';
import { getImageUrl } from '../utils/imageUtils';

const Home = () => {
  const { gigs, loading, error, getGigs } = useGigs();
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [priceRange, setPriceRange] = useState({ min: '', max: '' });
  const [filteredGigs, setFilteredGigs] = useState([]);

  // Categories for filtering
  const categories = [
    'Web Development',
    'Mobile Development',
    'Design & Creative',
    'Writing & Translation',
    'Digital Marketing',
    'Video & Animation',
    'Music & Audio',
    'Programming & Tech',
    'Business',
    'Lifestyle',
    'Other'
  ];

  // Fetch gigs on component mount
  useEffect(() => {
    getGigs();
  }, [getGigs]);

  // Filter gigs based on search term, category, and price range
  useEffect(() => {
    let filtered = gigs;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(gig =>
        gig.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        gig.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        gig.tags?.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Filter by category
    if (selectedCategory) {
      filtered = filtered.filter(gig => gig.category === selectedCategory);
    }

    // Filter by price range
    if (priceRange.min || priceRange.max) {
      filtered = filtered.filter(gig => {
        const price = gig.price;
        const minPrice = priceRange.min ? Number(priceRange.min) : 0;
        const maxPrice = priceRange.max ? Number(priceRange.max) : Infinity;
        return price >= minPrice && price <= maxPrice;
      });
    }

    setFilteredGigs(filtered);
  }, [gigs, searchTerm, selectedCategory, priceRange]);

  // Clear all filters
  const clearFilters = () => {
    setSearchTerm('');
    setSelectedCategory('');
    setPriceRange({ min: '', max: '' });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="bg-blue-600 text-white">
        <div className="max-w-7xl mx-auto py-12 px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl font-bold mb-4">
              Freelance Marketplace
            </h1>
            <p className="text-xl mb-8">
              Connect with talented freelancers and find amazing opportunities
            </p>
            <div className="flex justify-center space-x-4">
              <Link
                to="/register"
                className="bg-white text-blue-600 font-bold py-3 px-6 rounded-lg hover:bg-gray-100 transition-colors"
              >
                Find Freelancers
              </Link>
              <Link
                to="/register"
                className="border-2 border-white text-white font-bold py-3 px-6 rounded-lg hover:bg-white hover:text-blue-600 transition-colors"
              >
                Post a Job
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex space-x-6">
              <Link to="/gigs" className="text-gray-600 hover:text-gray-900">
                Browse All Gigs
              </Link>
              <Link to="/login" className="text-gray-600 hover:text-gray-900">
                Sign In
              </Link>
              <Link to="/register" className="text-gray-600 hover:text-gray-900">
                Sign Up
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Search and Filters Section */}
      <div className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
        <div className="bg-white rounded-lg shadow-sm p-6 mb-8">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Find Your Perfect Gig</h2>
          
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
            {/* Search Input */}
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Search
              </label>
              <input
                type="text"
                placeholder="Search gigs, skills, or keywords..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              />
            </div>

            {/* Category Filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Category
              </label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="">All Categories</option>
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>

            {/* Price Range */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Price Range
              </label>
              <div className="flex space-x-2">
                <input
                  type="number"
                  placeholder="Min"
                  value={priceRange.min}
                  onChange={(e) => setPriceRange(prev => ({ ...prev, min: e.target.value }))}
                  className="w-full px-2 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
                <input
                  type="number"
                  placeholder="Max"
                  value={priceRange.max}
                  onChange={(e) => setPriceRange(prev => ({ ...prev, max: e.target.value }))}
                  className="w-full px-2 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>
          </div>

          {/* Filter Summary and Clear Button */}
          <div className="flex justify-between items-center">
            <div className="text-sm text-gray-600">
              Showing {filteredGigs.length} of {gigs.length} gigs
              {searchTerm && (
                <span className="ml-2 px-2 py-1 bg-blue-100 text-blue-800 rounded">
                  "{searchTerm}"
                </span>
              )}
              {selectedCategory && (
                <span className="ml-2 px-2 py-1 bg-green-100 text-green-800 rounded">
                  {selectedCategory}
                </span>
              )}
              {(priceRange.min || priceRange.max) && (
                <span className="ml-2 px-2 py-1 bg-purple-100 text-purple-800 rounded">
                  ${priceRange.min || 0} - ${priceRange.max || '∞'}
                </span>
              )}
            </div>
            {(searchTerm || selectedCategory || priceRange.min || priceRange.max) && (
              <button
                onClick={clearFilters}
                className="text-sm text-blue-600 hover:text-blue-800 font-medium"
              >
                Clear all filters
              </button>
            )}
          </div>
        </div>

        {/* Gigs Grid */}
        {loading ? (
          <div className="flex justify-center items-center py-12">
            <div className="text-xl text-gray-600">Loading gigs...</div>
          </div>
        ) : error ? (
          <div className="bg-red-50 border border-red-200 rounded-md p-4 mb-6">
            <p className="text-sm text-red-600">{error}</p>
          </div>
        ) : filteredGigs.length === 0 ? (
          <div className="text-center py-12">
            <h3 className="text-lg font-medium text-gray-900 mb-4">
              {gigs.length === 0 ? 'No gigs available yet' : 'No gigs match your filters'}
            </h3>
            <p className="text-gray-600 mb-6">
              {gigs.length === 0 
                ? 'Be the first to create a gig!' 
                : 'Try adjusting your search criteria or browse all gigs.'
              }
            </p>
            {gigs.length === 0 ? (
              <Link
                to="/register"
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Sign Up to Create a Gig
              </Link>
            ) : (
              <button
                onClick={clearFilters}
                className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
              >
                Clear Filters
              </button>
            )}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredGigs.slice(0, 12).map((gig) => (
              <Link
                key={gig._id}
                to={`/gigs/${gig._id}`}
                className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow overflow-hidden"
              >
                {/* Gig Image */}
                {gig.images && gig.images.length > 0 ? (
                  <img
                    className="w-full h-48 object-cover"
                    src={getImageUrl(gig.images[0])}
                    alt={gig.title}
                  />
                ) : (
                  <div className="w-full h-48 bg-gray-200 flex items-center justify-center">
                    <span className="text-gray-500 text-sm">No image</span>
                  </div>
                )}

                {/* Gig Content */}
                <div className="p-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">
                    {gig.title}
                  </h3>
                  <p className="text-sm text-gray-600 mb-3 line-clamp-3">
                    {gig.description}
                  </p>
                  
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-lg font-bold text-green-600">
                      Starting at ${gig.price}
                    </span>
                    <span className="text-xs px-2 py-1 bg-blue-100 text-blue-800 rounded">
                      {gig.category}
                    </span>
                  </div>
                  
                  <div className="flex justify-between items-center text-sm text-gray-500">
                    <span>By {gig.seller?.name}</span>
                    <span>{gig.deliveryTime} days delivery</span>
                  </div>

                  {/* Tags */}
                  {gig.tags && gig.tags.length > 0 && (
                    <div className="flex flex-wrap gap-1 mt-2">
                      {gig.tags.slice(0, 3).map((tag, index) => (
                        <span
                          key={index}
                          className="text-xs px-2 py-1 bg-gray-100 text-gray-600 rounded"
                        >
                          {tag}
                        </span>
                      ))}
                      {gig.tags.length > 3 && (
                        <span className="text-xs text-gray-500">
                          +{gig.tags.length - 3} more
                        </span>
                      )}
                    </div>
                  )}
                </div>
              </Link>
            ))}
          </div>
        )}

        {/* Show More Button */}
        {filteredGigs.length > 12 && (
          <div className="text-center mt-8">
            <Link
              to="/gigs"
              className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg"
            >
              View All {filteredGigs.length} Gigs
            </Link>
          </div>
        )}
      </div>

      {/* Footer */}
      <footer className="bg-gray-800 text-white">
        <div className="max-w-7xl mx-auto py-8 px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h3 className="text-lg font-semibold mb-4">Freelance Marketplace</h3>
            <p className="text-gray-400">
              Connecting talent with opportunity
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Home;
