import React, { useEffect, useState } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { useGigs } from '../context/GigContext';
import { useAuth } from '../context/AuthContext';
import { getImageUrl } from '../utils/imageUtils';

const GigDetails = () => {
  const { id } = useParams();
  const { currentGig, loading, error, getGig } = useGigs();
  const { user, isAuthenticated } = useAuth();
  const navigate = useNavigate();
  const [selectedImage, setSelectedImage] = useState(0);
  const [showPurchaseModal, setShowPurchaseModal] = useState(false);
  const [purchaseLoading, setPurchaseLoading] = useState(false);

  useEffect(() => {
    if (id) {
      getGig(id);
    }
  }, [id, getGig]); // Added getGig back since it's now stable

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-xl">Loading gig details...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Error</h2>
          <p className="text-red-600 mb-4">{error}</p>
          <Link
            to="/gigs"
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Back to Gigs
          </Link>
        </div>
      </div>
    );
  }

  if (!currentGig) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Gig Not Found</h2>
          <Link
            to="/gigs"
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Back to Gigs
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <div className="mb-6">
            <Link
              to="/gigs"
              className="text-blue-600 hover:text-blue-800 font-medium"
            >
              ← Back to Gigs
            </Link>
          </div>

          <div className="bg-white overflow-hidden shadow rounded-lg">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 p-6">
              {/* Images */}
              <div>
                {currentGig.images && currentGig.images.length > 0 ? (
                  <div className="space-y-4">
                    <img
                      className="w-full h-64 object-cover rounded-lg"
                      src={getImageUrl(currentGig.images[0])}
                      alt={currentGig.title}
                    />
                    {currentGig.images.length > 1 && (
                      <div className="grid grid-cols-4 gap-2">
                        {currentGig.images.slice(1).map((image, index) => (
                          <img
                            key={index}
                            className="w-full h-16 object-cover rounded"
                            src={getImageUrl(image)}
                            alt={`${currentGig.title} ${index + 2}`}
                          />
                        ))}
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="w-full h-64 bg-gray-200 rounded-lg flex items-center justify-center">
                    <span className="text-gray-500">No image available</span>
                  </div>
                )}
              </div>

              {/* Details */}
              <div className="space-y-6">
                <div>
                  <h1 className="text-3xl font-bold text-gray-900 mb-4">
                    {currentGig.title}
                  </h1>
                  <div className="flex items-center space-x-4 mb-4">
                    <span className="text-2xl font-bold text-green-600">
                      ${currentGig.price}
                    </span>
                    <span className="px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
                      {currentGig.category}
                    </span>
                  </div>
                </div>

                <div>
                  <h3 className="text-lg font-medium text-gray-900 mb-2">
                    About This Gig
                  </h3>
                  <p className="text-gray-600 leading-relaxed">
                    {currentGig.description}
                  </p>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <h4 className="text-sm font-medium text-gray-900">Delivery Time</h4>
                    <p className="text-gray-600">{currentGig.deliveryTime} days</p>
                  </div>
                  <div>
                    <h4 className="text-sm font-medium text-gray-900">Seller</h4>
                    <p className="text-gray-600">{currentGig.seller?.name}</p>
                  </div>
                </div>

                {currentGig.tags && currentGig.tags.length > 0 && (
                  <div>
                    <h4 className="text-sm font-medium text-gray-900 mb-2">Tags</h4>
                    <div className="flex flex-wrap gap-2">
                      {currentGig.tags.map((tag, index) => (
                        <span
                          key={index}
                          className="px-2 py-1 bg-gray-100 text-gray-700 rounded text-sm"
                        >
                          {tag}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                <div className="pt-6">
                  <button className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-6 rounded-lg">
                    Contact Seller
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GigDetails;
